package MavenProject.Testing;

public class While {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	    int i=1;
	    while(i<=10){ 
	        System.out.println(i); 
	    i++; 
	    } 
	    
	    System.out.println("****************************************************");
	    for(int j=1;j<=10;j++) {
	    	System.out.println(j);
	    }
   
	    do{    
	        System.out.println(i); 
	    i++; 
	    }while(i<=10);

	}

}
